import React, { useRef, useState } from "react";
import "../css/shape-rectangle.css";

export const ShapeCircle = () => {
  const canvasRef = useRef(null);
  const [isDrawing, setIsDrawing] = useState(false);
  const [startPos, setStartPos] = useState({ x: 0, y: 0 });
  const [circles, setCircles] = useState([]);

  const handleMouseDown = (e) => {
    const rect = canvasRef.current.getBoundingClientRect();
    setStartPos({
      x: e.clientX - rect.left,
      y: e.clientY - rect.top,
    });
    setIsDrawing(true);
  };

  const handleMouseMove = (e) => {
    if (!isDrawing) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const rect = canvas.getBoundingClientRect();
    const currentX = e.clientX - rect.left;
    const currentY = e.clientY - rect.top;

    ctx.clearRect(0, 0, canvas.width, canvas.height);

    circles.forEach((circle) => {
      ctx.beginPath();
      ctx.arc(
        circle.x,
        circle.y,
        Math.abs(circle.endPos.x - circle.startPos.x) / 2,
        0,
        180
      );
      ctx.strokeStyle = "#ffffff";
      ctx.lineWidth = 2;
      ctx.stroke();
    });

    const width = currentX - startPos.x;
    ctx.beginPath();
    ctx.arc(startPos.x, startPos.y, Math.abs(width / 2), 0, 360);
    ctx.strokeStyle = "#ffffff";
    ctx.lineWidth = 2;
    ctx.stroke();
  };

  const handleMouseUp = (e) => {
    if (!isDrawing) return;
    setIsDrawing(false);

    const rect = canvasRef.current.getBoundingClientRect();
    const endPos = {
      x: e.clientX,
      y: e.clientY,
    };

    // Yeni çizilen Circle'i kaydet
    const newCircle = {
      x: startPos.x,
      y: startPos.y,
      width: endPos.x - startPos.x,
      height: endPos.y - startPos.y,
    };

    setCircles([...circles, newCircle]); // Circle'i diziye ekle
  };

  return (
    <canvas
      width={"1200px"}
      height={"800px"}
      ref={canvasRef}
      className="shape"
      onMouseDown={handleMouseDown}
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
    />
  );
};
