import React, { useState, useEffect } from "react";
import { GetPositionData } from "../../Positions/CustomDragPositionData";

function CustomDraggable({ onMouseDown, id }) {
  const positionData = GetPositionData(id);
  const [toggle, setToggle] = useState(false);

  const forceUpdate = () => setToggle(!toggle);

  const handleUpdatePosition = () => {
    // Yeni pozisyon değerlerini positionData'dan al
    const { x, y } = positionData;
    document.getElementById(
      `${id}`
    ).style.transform = `translate(${x}px, ${y}px)`;
    forceUpdate();
  };

  useEffect(() => {
    handleUpdatePosition();
  }, [positionData.x, positionData.y]);

  return (
    <div>
      <div
        onMouseDown={onMouseDown}
        id={`${id}`}
        style={{
          position: "absolute",
          transform: `translate(${positionData.x}px, ${positionData.y}px)`,
        }}
      >
        Drag Me
      </div>
    </div>
  );
}

export default CustomDraggable;
