import React, { useState } from "react";
import "../css/hover-menu-items-shape.css";
import Arrow from "../ShapeForms/Arrow.png";
import Circle from "../ShapeForms/Circle.png";
import Line from "../ShapeForms/Line.png";
import Rectangle from "../ShapeForms/Rectangle.png";
import Dash_Arrow from "../ShapeForms/Dash_Arrow.png";
import Dash_Circle from "../ShapeForms/Dash_Circle.png";
import Dash_Line from "../ShapeForms/Dash_Line.png";
import Dash_Rectangle from "../ShapeForms/Dash_Rectangle.png";

export const HoverMenuItems_Shapes = () => {
  const shapes = [
    { name: "Arrow", img: Arrow, whatItDoes: () => {} },
    { name: "Rectangle", img: Rectangle, whatItDoes: () => {} },
    { name: "Line", img: Line, whatItDoes: () => {} },
    { name: "Circle", img: Circle, whatItDoes: () => {} },
    { name: "DashedArrow", img: Dash_Arrow, whatItDoes: () => {} },
    { name: "DashedRectangle", img: Dash_Rectangle, whatItDoes: () => {} },
    { name: "DashedLine", img: Dash_Line, whatItDoes: () => {} },
    { name: "DashedCircle", img: Dash_Circle, whatItDoes: () => {} },
  ];
  return (
    <div className="container">
      {shapes.map((item, _) => (
        <ButtonComponent>{item.name}</ButtonComponent>
      ))}
    </div>
  );
};

const ButtonComponent = (img, name, event) => {
  const [drawing, setDrawing] = useState(false);

  return (
    <>
      <button
        style={{ backgroundImage: img }}
        onMouseDown={() => {
          setDrawing(!drawing);
          event();
        }}
      >
        {name}
      </button>
    </>
  );
};
