import React, { useState, useRef } from "react";
import "../css/navigationBar.css";
import { useSpring, animated } from "react-spring";

export const NavigationButtons = ({
  name,
  onClickEvent,
  customStyle,
  vHover,
}) => {
  const [hover, setHover] = useState(false);

  const menuRef = useRef(null);
  const anim = useSpring({
    opacity: hover ? 1 : 0,
    transform: hover ? "translateY(0)" : "translateY(-20px)",
    display: hover ? "flex" : "none",
  });
  const customMenu = (
    <animated.div className="hover-menu" style={{ ...anim }}>
      <ul className="hover-menu-item-parent">
        <li className="hover-menu-item">sa</li>
      </ul>
    </animated.div>
  );
  const [bgColor, setBgColor] = useState("#333647");
  const hoverHandler = () => {
    console.log("hovering");
    setHover(true);
    vHover(true);
  };
  const exitHover = () => {
    console.log("exit hover");
    setHover(false);
    vHover(false);
  };

  const leaveFunction = (e) => {
    setBgColor("#333647");
    handleMouseLeave(e);
    exitHover();
  };
  const enterFunction = () => {
    hoverHandler();
    setBgColor("#7800e0");
  };
  const clickFunction = () => {
    if (onClickEvent === undefined) return;
    onClickEvent();
  };

  const handleMouseLeave = (e) => {
    const menuRect = menuRef.current.getBoundingClientRect();

    // Menü dışına çıkıldığında yalnızca kapanacak
    const isMouseInsideMenu =
      menuRect.left <= e.clientX &&
      menuRect.right >= e.clientX &&
      menuRect.top <= e.clientY &&
      menuRect.bottom >= e.clientY;

    if (!isMouseInsideMenu) {
      exitHover();
    }
  };

  return (
    <>
      <div>
        <button
          ref={menuRef}
          className="item nav-button"
          onMouseDown={clickFunction}
          onMouseLeave={(e) => leaveFunction(e)}
          onMouseEnter={enterFunction}
          style={{ ...customStyle, backgroundColor: bgColor }}
        >
          {name}
        </button>
        {customMenu}
      </div>
    </>
  );
};
