import React from "react";
import "../css/propListMenu.css";
import "../css/player-prop-list.css";
import { useSpring, animated } from "react-spring";

import { ChildInMenuItem } from "./ChildInMenuItem";
export const PlayerPropsList = ({
  onMouseEnterHandler,
  onMouseLeaveHandler,
  onMouseDownHandler,
  childs,
  showPropList,
  styles,
}) => {
  const chilPropsList = () => {
    return childs.map((item, index) => {
      return (
        showPropList && (
          <ChildInMenuItem
            key={index} // `key` eklemek, liste elemanları için gerekli
            onMouseDownHandler={onMouseDownHandler}
            onMouseEnterHandler={onMouseEnterHandler}
            onMouseLeaveHandler={onMouseLeaveHandler}
            name={item[index]} // `item[index]` yerine `item` kullanmanız gerekebilir
          />
        )
      );
    });
  };
  return (
    <>
      <animated.div
        style={styles}
        className="child-menu"
        onMouseEnter={onMouseEnterHandler}
        onMouseLeave={onMouseLeaveHandler}
      >
        <ul
          onMouseEnter={onMouseEnterHandler}
          onMouseLeave={onMouseLeaveHandler}
        >
          {chilPropsList()}
        </ul>
      </animated.div>
    </>
  );
};
