import React, { useState, useRef } from "react";
import "../css/players.css";
import "../css/propListMenu.css";
import "../css/player-prop-list.css";
import Vector2 from "../Components/Vector2";
import { InMenuItem } from "./InMenuItem";
import { useSpring, animated } from "react-spring";
import Draggable from "react-draggable";

//call this function when clicked to Tactic Selection

export const ChangePlayerData = (positions, roles) => {
  return {
    selectedChildFeature: "",
    props: {
      coordinate: new Vector2(positions.x, positions.y),
      datas: [
        { name: "Kaleci", shortName: "K", Props: ["Savunma"] },
        {
          name: "Libero Kaleci",
          shortName: "LK",
          Props: ["Savunma", "Destek", "Hücum"],
        },
      ],
    },
    selectedFeature: "",
  };
};

export const SoccerPlayer = ({ onDragHandlerEvent, defaultPosition }) => {
  const { selectedChildFeature, props, selectedFeature } = ChangePlayerData();

  const [showMenuState, setShowMenu] = useState(false);
  const [selectedChildFeatureState, setSelectedChildFeature] =
    useState(selectedChildFeature);
  const [propsState, setProps] = useState(props);
  const [selectedFeatureState, setSelectedFeature] = useState(selectedFeature);
  const [colorsState, setColors] = useState({
    backgroundColor: "#ffffff",
    borderColor: "#ffffff",
    color: "#7800e0",
  });
  const anim = useSpring({
    opacity: showMenuState ? 1 : 0,
    transform: showMenuState ? "translateY(0)" : "translateY(-10px)",
    display: showMenuState ? "block" : "none",
  });
  const handleSelectedChildFeature = (name) => {
    setSelectedChildFeature(name);
  };

  const handleSelectedFeature = (name) => {
    setSelectedFeature(name);
  };
  const dragginFunc = (e) => {
    setProps(onDragHandlerEvent(e.clientX, e.clientY));
    if (propsState !== undefined) {
      handleSelectedChildFeature(
        propsState.datas[0].name
          .split(" ")
          .map((word) => word.charAt(0).toUpperCase())
      );
      handleSelectedFeature(propsState.datas[0].shortName);
    }
  };
  function a() {
    if (propsState === undefined) {
      return;
    } else {
      return propsState.datas.map((item, index) => (
        <InMenuItem
          key={index}
          shortName={item.shortName}
          name={item.name}
          childProps={item.Props}
          propsState={propsState}
          handleSelectedChildFeature={handleSelectedChildFeature}
          handleSelectedFeature={handleSelectedFeature}
        />
      ));
    }
  }
  const playerRef = useRef(null); // Referansı tanımlıyoruz
  const menuRef = useRef(null); // Menü için referans
  const handleMouseEnter = () => {
    setShowMenu(true);
  };
  const handleMouseLeave = (e) => {
    // Menü dışına çıkıldığında yalnızca kapanacak
    const playerRect = playerRef.current.getBoundingClientRect();
    const menuRect = menuRef.current.getBoundingClientRect();
    const isMouseInsideMenu =
      menuRect.left <= e.clientX &&
      menuRect.right >= e.clientX &&
      menuRect.top <= e.clientY &&
      menuRect.bottom >= e.clientY;
    const isMouseInsidePlayer =
      playerRect.left <= e.clientX &&
      playerRect.right >= e.clientX &&
      playerRect.top <= e.clientY &&
      playerRect.bottom >= e.clientY;

    if (!isMouseInsideMenu && !isMouseInsidePlayer) {
      setShowMenu(false);
    }
  };
  return (
    <>
      <Draggable onDrag={dragginFunc} defaultPosition={defaultPosition}>
        <div
          className="player-container"
          onMouseEnter={() => handleMouseEnter()}
          onMouseLeave={(e) => handleMouseLeave(e)}
        >
          <div
            ref={playerRef}
            className="soccerplayer-background"
            style={colorsState}
            onMouseDown={() => {
              handleMouseClick();
              if (!showMenuState) {
                setColors({
                  backgroundColor: "#ffffff",
                  borderColor: "#ffffff",
                  color: "#7800e0",
                });
              } else {
                setColors({
                  backgroundColor: "#7800e0",
                  borderColor: "#7133B2",
                  color: "#ffffff",
                });
              }
            }}
            onMouseEnter={() => {
              setColors({
                backgroundColor: "#7800e0",
                borderColor: "#7133B2",
                color: "#ffffff",
              });
            }}
            onMouseLeave={(e) => {
              setColors({
                backgroundColor: "#ffffff",
                borderColor: "#ffffff",
                color: "#7800e0",
              });
            }}
          >
            <span className="info">{selectedFeatureState}</span>
            <span className="info-child">{selectedChildFeatureState}</span>
          </div>
          {
            // Burada showMenuState kontrolü ekle
            <animated.div
              className="menu"
              style={anim}
              onMouseEnter={() => handleMouseEnter()}
              onMouseLeave={(e) => handleMouseLeave(e)}
            >
              <ul className="in-menu" ref={menuRef}>
                {a()}
              </ul>
            </animated.div>
          }
        </div>
      </Draggable>
    </>
  );
};
