import React, { useState } from "react";

export const ButtonComponent = (img, name, event) => {
  const [drawing, setDrawing] = useState(false);

  return (
    <>
      <button
        style={{ backgroundImage: img }}
        onMouseDown={() => {
          setDrawing(!drawing);
          event();
        }}
      >
        {name}
      </button>
    </>
  );
};
