import React, { useState } from "react";
import "../css/players.css";
import "../css/propListMenu.css";
import "../css/player-prop-list.css";
import Vector2 from "../Components/Vector2";
import { InMenuItem } from "./InMenuItem";

import Draggable from "react-draggable";

export const SoccerPlayer = ({ onDragHandler }) => {
  const [showMenu, setShowMenu] = useState(false);
  const [selectedChildFeature, setSelectedChildFeature] = useState("Savunma");
  const [props, setProps] = useState({
    coordinate: new Vector2(0, 0),
    datas: [
      { name: "Kaleci", shortName: "K", Props: ["Savunma"] },
      {
        name: "Libero Kaleci",
        shortName: "LK",
        Props: ["Savunma", "Destek", "H端cum"],
      },
    ],
  });
  const [selectedFeature, setSelectedFeature] = useState("N/A");
  const [colors, setColors] = useState({
    backgroundColor: "#ffffff",
    borderColor: "#ffffff",
    color: "#7800e0",
  });

  const handleSelectedChildFeature = (name) => {
    setSelectedChildFeature(name);
  };

  const handleSelectedFeature = (name) => {
    setSelectedFeature(name);
  };
  const handleMouseClick = () => {
    setShowMenu(!showMenu);
  };

  const dragginFunc = (e) => {
    setProps(onDragHandler(e.clientX, e.clientY));
    if (props !== undefined) {
      handleSelectedChildFeature(
        props.datas[0].name
          .split(" ")
          .map((word) => word.charAt(0).toUpperCase())
      );
      handleSelectedFeature(props.datas[0].shortName);
    }
  };
  function a() {
    if (props === undefined) {
      return;
    } else {
      return props.datas.map(
        (item, index) =>
          showMenu && (
            <InMenuItem
              key={index}
              shortName={item.shortName}
              name={item.name}
              childProps={item.Props}
              props={props}
              handleSelectedChildFeature={handleSelectedChildFeature}
              handleSelectedFeature={handleSelectedFeature}
            />
          )
      );
    }
  }

  return (
    <>
      <Draggable onDrag={dragginFunc}>
        <div className="player-container">
          <div
            className="soccerplayer-background"
            style={colors}
            onMouseDown={() => {
              handleMouseClick();
              if (!showMenu) {
                setColors({
                  backgroundColor: "#ffffff",
                  borderColor: "#ffffff",
                  color: "#7800e0",
                });
              } else {
                setColors({
                  backgroundColor: "#7800e0",
                  borderColor: "#7133B2",
                  color: "#ffffff",
                });
              }
            }}
            onMouseEnter={() => {
              setColors({
                backgroundColor: "#7800e0",
                borderColor: "#7133B2",
                color: "#ffffff",
              });
            }}
            onMouseLeave={() => {
              setColors({
                backgroundColor: "#ffffff",
                borderColor: "#ffffff",
                color: "#7800e0",
              });
            }}
          >
            <span className="info">{selectedFeature}</span>
            <span className="info-child">{selectedChildFeature}</span>
          </div>
          {showMenu && ( // Burada showMenu kontrol端 ekle
            <div className="menu">
              <ul className="in-menu">{a()}</ul>
            </div>
          )}
        </div>
      </Draggable>
    </>
  );
};
