import React, { useState } from "react";
import "../css/players.css";
import "../css/propListMenu.css";
import "../css/player-prop-list.css";
import Draggable from "react-draggable";
import { PlayerPropsList } from "./playerPropsList";

export const SoccerPlayer = () => {

  let [displayMode, setDisplayMode] = useState("none");

  return (
    <>
      <Draggable>
        <div className="player-container">
          <div className="soccerplayer-background"></div>
          <div className="menu">
            <ul className="in-menu" onMouseEnter={setDisplayMode("block")} onMouseLeave={setDisplayMode("none")}>
              <li className="in-menu-item">
                Kaleci
              </li>
              <li className="in-menu-item">
                Libero Kaleci
              </li>
            </ul>            
          </div>
          <PlayerPropsList displayType={displayMode}/>
        </div>
      </Draggable>
    </>
  );
};
