            <NavigationButtons
              name="Arka Plan"
              customStyle={{ width: 163, height: 48 }}
            />
            <NavigationButtons
              name="Kalem"
              customStyle={{ width: 163, height: 48 }}
            />
            <NavigationButtons
              name="Şekiller"
              customStyle={{ width: 170, height: 48 }}
            />
            <NavigationButtons
              name="Görseli İndir"
              customStyle={{ width: 170, height: 48 }}
