            <img
              src={bg}
              style={{
                width: 1200,
                height: 800,
                display: "grid",
                borderRadius: 5,
              }}
            ></img>
            {firstPositions.map((item, _) => {
              return (
                <SoccerPlayer
                  onDragHandler={setCorrectData}
                  position={{ x: item.x, y: item.y }}
