import React from "react";
import "../css/propListMenu.css";
import "../css/player-prop-list.css";
import { ChildInMenuItem } from "./ChildInMenuItem";

export const PlayerPropsList = ({
  onMouseEnterHandler,
  onMouseLeaveHandler,
  onMouseDownHandler,
  childs,
  showPropList, // Direkt kontrol amaçlı ekledik
}) => {
  const chilPropsList = () => {
    return childs.map((item, index) => (
      <ChildInMenuItem
        key={index}
        onMouseDownHandler={onMouseDownHandler}
        onMouseEnterHandler={onMouseEnterHandler}
        onMouseLeaveHandler={onMouseLeaveHandler}
        name={item}
      />
    ));
  };

  return (
    <div
      className="child-menu"
      style={{ display: showPropList ? "block" : "none" }} // CSS ile kontrol
      onMouseEnter={onMouseEnterHandler}
      onMouseLeave={onMouseLeaveHandler}
    >
      <ul>{chilPropsList()}</ul>
    </div>
  );
};
