import React, { useState } from "react";

function DrawSquare() {
  const [isDrawing, setIsDrawing] = useState(false);
  const [startPos, setStartPos] = useState({ x: 0, y: 0 });
  const [endPos, setEndPos] = useState({ x: 0, y: 0 });
  const [squares, setSquares] = useState([]);

  const handleMouseDown = (e) => {
    setIsDrawing(true);
    setStartPos({ x: e.clientX, y: e.clientY });
    setEndPos({ x: e.clientX, y: e.clientY });
  };

  const handleMouseMove = (e) => {
    if (!isDrawing) return;
    setEndPos({ x: e.clientX, y: e.clientY });
  };

  const handleMouseUp = () => {
    setIsDrawing(false);
    // Yeni kareyi squares listesine ekleyelim
    setSquares((prevSquares) => [
      ...prevSquares,
      {
        startPos,
        endPos,
      },
    ]);
  };

  return (
    <div
      onMouseDown={handleMouseDown}
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
      style={{
        width: "100vw",
        height: "100vh",
        position: "absolute",
      }}
    >
      {/* Çizilen tüm kareleri render edelim */}
      {squares.map((square, index) => (
        <div
          key={index}
          style={{
            position: "absolute",
            left: Math.min(square.startPos.x, square.endPos.x),
            top: Math.min(square.startPos.y, square.endPos.y),
            width: Math.abs(square.endPos.x - square.startPos.x),
            height: Math.abs(square.endPos.y - square.startPos.y),
            backgroundColor: "rgba(0, 150, 255, 0.5)",
            border: "2px solid blue",
            textAlign: "center",
            justifyContent: "center",
            alignContent: "center",
          }}
        >
          startPos: ({square.startPos.x},{square.startPos.y}) endPos: (
          {square.endPos.x},{square.endPos.y})
        </div>
      ))}

      {/* Şu an çizilmekte olan kare */}
      {isDrawing && (
        <div
          style={{
            position: "absolute",
            left: Math.min(startPos.x, endPos.x),
            top: Math.min(startPos.y, endPos.y),
            width: Math.abs(endPos.x - startPos.x),
            height: Math.abs(endPos.y - startPos.y),
            backgroundColor: "rgba(0, 150, 255, 0.5)",
            border: "2px solid blue",
            textAlign: "center",
            justifyContent: "center",
            alignContent: "center",
          }}
        >
          startPos: ({startPos.x},{startPos.y}) endPos: ({endPos.x},{endPos.y})
        </div>
      )}
    </div>
  );
}

export default DrawSquare;
